Consider the following Node struct definition for a singly linked list:struct Node {
    int data;
    Node* next;
};

What will be the output of the following C++ code snippet?#include <iostream>

struct Node {
    int data;
    Node* next;
};

int main() {
    Node* head = nullptr;
    head = new Node;
    head->data = 10;
    head->next = nullptr;
    std::cout << head->data << std::endl;
    return 0;
}

What does the following C++ code snippet do?Node* reverseList(Node* head) {
    Node *prev = nullptr, *curr = head, *next = nullptr;
    while (curr != nullptr) {
        next = curr->next;
        curr->next = prev;
        prev = curr;
        curr = next;
    }
    return prev;
}

What will be the output of the following C++ code snippet?#include <iostream>

struct Node {
    int data;
    Node* next;
};

void printList(Node* head) {
    while (head != nullptr) {
        std::cout << head->data << " ";
        head = head->next;
    }
    std::cout << std::endl;
}

int main() {
    Node* head = nullptr;
    head = new Node;
    head->data = 10;
    head->next = nullptr;
    Node* second = new Node;
    second->data = 20;
    second->next = nullptr;
    head->next = second;
    printList(head);
    return 0;
}

What does the following C++ code snippet do?void deleteList(Node* head) {
    Node* temp;
    while (head != nullptr) {
        temp = head;
        head = head->next;
        delete temp;
    }
}

What does the following C++ code snippet do?bool detectLoop(Node* head) {
    Node *slow = head, *fast = head;
    while (fast != nullptr && fast->next != nullptr) {
        slow = slow->next;
        fast = fast->next->next;
        if (slow == fast) {
            return true;
        }
    }
    return false;
}

What will be the output of the following C++ code snippet?#include <iostream>

struct Node {
    int data;
    Node* next;
};

void printList(Node* head) {
    while (head != nullptr) {
        std::cout << head->data << " ";
        head = head->next;
    }
    std::cout << std::endl;
}

void deleteList(Node* head) {
    if (head == nullptr) return;
    deleteList(head->next);
    delete head;
}

int main() {
    Node* head = new Node;
    head->data = 10;
    head->next = nullptr;
    Node* second = new Node;
    second->data = 20;
    second->next = nullptr;
    head->next = second;
    deleteList(head);
    printList(head);
    return 0;
}

What does the following C++ code snippet do?Node* mergeLists(Node* list1, Node* list2) {
    if (list1 == nullptr) return list2;
    if (list2 == nullptr) return list1;
    if (list1->data < list2->data) {
        list1->next = mergeLists(list1->next, list2);
        return list1;
    } else {
        list2->next = mergeLists(list1, list2->next);
        return list2;
    }
}

What does the following C++ code snippet do?Node* findIntersection(Node* list1, Node* list2) {
    Node* head = nullptr;
    Node* tail = nullptr;
    while (list1 != nullptr && list2 != nullptr) {
        if (list1->data == list2->data) {
            if (head == nullptr) {
                head = tail = new Node;
                head->data = list1->data;
            } else {
                tail->next = new Node;
                tail = tail->next;
                tail->data = list1->data;
            }
            list1 = list1->next;
            list2 = list2->next;
        } else if (list1->data < list2->data) {
            list1 = list1->next;
        } else {
            list2 = list2->next;
        }
    }
    return head;
}

What will be the output of the following C++ code snippet?#include <iostream>

struct Node {
    int data;
    Node* next;
};

Node* deleteAlternateNodes(Node* head) {
    Node* current = head;
    while (current != nullptr && current->next != nullptr) {
        Node* temp = current->next;
        current->next = temp->next;
        delete temp;
        current = current->next;
    }
    return head;
}

void printList(Node* head) {
    while (head != nullptr) {
        std::cout << head->data << " ";
        head = head->next;
    }
    std::cout << std::endl;
}

int main() {
    Node* head = new Node;
    head->data = 10;
    head->next = new Node;
    head->next->data = 20;
    head->next->next = new Node;
    head->next->next->data = 30;
    head->next->next->next = nullptr;
    head = deleteAlternateNodes(head);
    printList(head);
    return 0;
}

What does the following C++ code snippet do?Node* reverseKGroup(Node* head, int k) {
    Node* current = head;
    Node* next = nullptr;
    Node* prev = nullptr;
    int count = 0;

    while (current != nullptr && count < k) {
        next = current->next;
        current->next = prev;
        prev = current;
        current = next;
        count++;
    }

    if (next != nullptr) {
        head->next = reverseKGroup(next, k);
    }

    return prev;
}

What does the following C++ code snippet do?Node* deleteDuplicates(Node* head) {
    Node* current = head;
    while (current != nullptr && current->next != nullptr) {
        if (current->data == current->next->data) {
            Node* temp = current->next;
            current->next = current->next->next;
            delete temp;
        } else {
            current = current->next;
        }
    }
    return head;
}
